import * as React from "react";
import { Admin, Resource, ListGuesser, fetchUtils } from 'react-admin';
import jsonServerProvider from 'ra-data-json-server';
import simpleRestProvider from 'ra-data-simple-rest';

import { PostList } from './posts';
import authProvider from './authProvider';

const fetchJson = (url, options = {}) => {
    options.user = {
        authenticated: true,
        token: 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjo3LCJ1c2VybmFtZSI6Imx5dGljYSIsImV4cCI6MTYzNzQ4MjQ3NCwiZW1haWwiOiJhZG1pbkBseXRpY2EuYWkiLCJvcmlnX2lhdCI6MTYzNzM5NjA3NH0.lzd2ap2SQNyOqN3keAg4Yt1Y6SbJgnENOLUwm2XEZJA'
    };
    return fetchUtils.fetchJson(url, options);
};
const dataProvider = simpleRestProvider('https://staging.vico.ai/analytics', fetchJson);
const App = () => (
    dataProvider.getList('stores', { id: [1] })
);


export default App;
