import * as React from "react";
import { useQuery, Loading, Error } from 'react-admin';
import { List, Datagrid, TextField, DateField, EmailField, SearchInput, Filter } from 'react-admin';


const httpClient = (url, options = {}) => {
    console.log(url)
    if (!options.headers) {
        options.headers = new Headers({ Accept: 'application/json' });
    }
    const { token } = JSON.parse(localStorage.getItem('auth'));
    options.headers.set('Authorization', `Bearer ${token}`);
    return fetchUtils.fetchJson(url, options);
};
const dataProvider = drfProvider('https://staging.vico.ai/analytics', httpClient);
const storeProvider = ({ userId }) => {
    const { data, loading, error } = useQuery({ 
        type: 'getMany',
        resource: 'stores',
        payload: { id: userId }
    });
    return (
        <List {...props}>
            <Datagrid rowClick="edit">
                <TextField source="id" />
                <TextField source="name" />
                <TextField source="assigned" />
                <TextField source="categories" />
                <TextField source="schedules" />
                <TextField source="time_zone" />
                <TextField source="identifier" />
            </Datagrid>
        </List>
    );
};


export default storeProvider;
