// in src/App.js
import * as React from "react";
import { Admin, Resource, ListGuesser, fetchUtils } from 'react-admin';
import lb4Provider from 'react-admin-lb4';
import jsonServerProvider from 'ra-data-json-server';
import simpleRestProvider from 'ra-data-simple-rest';

import StoreList from './posts';
import authProvider from './authProvider';

const httpClient = (url, options = {}) => {
    console.log(url)
    if (!options.headers) {
        options.headers = new Headers({ 'Content-Type': 'application/json' });
    }
    const { token } = JSON.parse(localStorage.getItem('auth'));
    options.headers.set('Authorization', `Bearer ${token}`);
    return fetchUtils.fetchJson(url, options);
};
// const dataProvider = jsonServerProvider('http://localhost:8000', httpClient);
const App = () => (
    <Admin dataProvider={lb4Provider('http://localhost:8000')} authProvider={authProvider}>
        <Resource name="stores" list={StoreList}/>
    </Admin>
    // dataProvider.getMany('stores', { id: [1] })
);


export default App;
